<?php	require('../include/config.inc.php');	if (get_status()!=MANAGER){		die('<font color="red">Only manager acceptable!</font>');	}	@$id=$_POST['id'];	@$name=$_POST['name'];	@$price_in=$_POST['price_in'];	@$price_out=$_POST['price_out'];	@$price_out_patch=$_POST['price_out_patch'];	echo $id.$name.$price_in.$price_out.$price_out_patch;	if (!isset($id) || !isset($name) || !isset($price_in) || !isset($price_out) || !isset($price_out_patch))		die('<font color="red">input information not complete!</font>');	$db=mysql_login_utfs8();	if (!$db)		die('<font color="red">Mysql access denied!</font>');	$result=$db->query("insert commodity values('$id','$name','0','$price_in','$price_out','$price_out_patch',NOW())");	if ($result)		$statement="新商品添加成功！";	else		$statement="新商品添加失败！可能原因包括商品编号已存在或数据类型填写错误！";	$url='..';	jump_page($statement,$url);		?>